mapper.select:
select
count(*) from car where c_del='n'

	and brand=#{brand} and c_kind=#{c_kind}





		and brand=#{brand}



		and c_kind=#{c_kind}




	order by #{order} desc

select *
from(select rowNum rn, a.* from (
select
c.cno,c.c_name,c.c_cover_img,c_content, AVG(r.rating) avg_rating
FROM
car c, review r where c.cno = r.cno(+) and c_del='n' and re_del(+)='n'

	and brand=#{brand} and c_kind=#{c_kind}





		and c_kind=#{c_kind}



		and brand=#{brand}


GROUP BY c.cno, c.c_name,c_content,c.c_cover_img

	order by nvl(${order},0) desc

)a)
where rn
between #{startRow} and #{endRow}

select * from (select rowNum rn, a.* from (
select * from car


		${search} like '%'||#{keyword}||'%'


)a) where
rn between #{startRow} and #{endRow}
