mapper.update:
update compliment set
cp_del='y'
where cpno=#{cpno}

update board set b_title =
#{b_title}, b_content = #{b_content}
where bno = #{bno}

mapper.select:
select * from ( select a.*, rowNum rn from ( select * from board
${search} like '%'|| #{keyword} ||'%'
order by bno desc ) a ) where rn between #{startRow} and #{endRow}
